public class User\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n    public virtual ICollection<Usergroup> BelongsTo { get; set; }\n\n    public User()\n    {\n        BelongsTo = new List<Usergroup>();\n    }\n}\n\npublic class Usergroup\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n    public virtual ICollection<User> Users { get; set; }\n\n    public Usergroup()\n    {\n        Users = new List<User>();\n    }\n}