public interface IContactInfo { }\n\npublic class Address : IContactInfo { }\npublic class Email : IContactInfo { }\npublic class Phone : IContactInfo { }\n\npublic class ContactManager\n{\n    private List<Address> m_Address = new List<Address>();\n    private List<Email> m_Email = new List<Email>();\n    private List<Phone> m_Phone = new List<Phone>();\n\n    public void Add<T>(T obj) where T : class, IContactInfo\n    {\n        if (obj is Address address)\n        {\n            m_Address.Add(address);\n        }\n        else if (obj is Email email)\n        {\n            m_Email.Add(email);\n        }\n        else if (obj is Phone phone)\n        {\n            m_Phone.Add(phone);\n        }\n        else\n        {\n            throw new ArgumentException("Unsupported type");\n        }\n    }\n}