public static void Main(string[] args)\n{\n    // Example tree setup\n    var root = new TreeNode { Id = 1, Name = "Root" };\n    var child1 = new TreeNode { Id = 2, Name = "Child1" };\n    var child2 = new TreeNode { Id = 3, Name = "Child2" };\n    var grandchild1 = new TreeNode { Id = 4, Name = "Grandchild1" };\n\n    // Building the tree\n    child1.Children.Add(grandchild1);\n    root.Children.Add(child1);\n    root.Children.Add(child2);\n\n    // Print the tree\n    PrintTree(root);\n}