<%@ Page Language="C#" %>\n   <%@ Import Namespace="System.Web.Extensions" %>\n   \n   <!DOCTYPE html>\n   <html>\n   <head>\n       <title>Test .NET 3.5 SP1</title>\n   </head>\n   <body>\n       <script runat="server">\n           protected void Page_Load(object sender, EventArgs e)\n           {\n               try\n               {\n                   // Use a feature from .NET 3.5 SP1 (e.g., the System.Web.Script.Serialization namespace)\n                   var serializer = new System.Web.Script.Serialization.JavaScriptSerializer();\n                   var json = serializer.Serialize(new { Test = "Hello, .NET 3.5 SP1!" });\n                   Response.Write("Serialization successful: " + json);\n               }\n               catch (Exception ex)\n               {\n                   Response.Write("Error: " + ex.Message);\n               }\n           }\n       </script>\n       <h1>Test .NET 3.5 SP1</h1>\n   </body>\n   </html>