using System;\nusing System.Data;\nusing System.Data.SqlClient;\nusing System.Web;\n\npublic void UpdateField(HttpRequest request)\n{\n    string connectionString = "your_connection_string";\n    string id = request.QueryString["id"];\n    string fieldName = request.QueryString["fieldname"];\n    string fieldValue = request.QueryString["fieldvalue"];\n\n    // Validate the column name to prevent SQL injection\n    string[] validColumns = { "title", "description", "anotherColumn" }; // Add your valid column names here\n    if (Array.IndexOf(validColumns, fieldName) == -1)\n    {\n        throw new ArgumentException("Invalid column name");\n    }\n\n    using (SqlConnection connection = new SqlConnection(connectionString))\n    {\n        string query = $"UPDATE MyTable SET {fieldName} = @fieldvalue WHERE id = @id";\n\n        using (SqlCommand cmd = new SqlCommand(query, connection))\n        {\n            cmd.Parameters.Add("@fieldvalue", SqlDbType.VarChar).Value = fieldValue; // Use appropriate SqlDbType\n            cmd.Parameters.Add("@id", SqlDbType.Int).Value = int.Parse(id);\n\n            connection.Open();\n            cmd.ExecuteNonQuery();\n        }\n    }\n}