using System;\nusing System.IO.Pipes;\nusing System.Security;\nusing System.Runtime.InteropServices;\n\npublic class ParentProcess\n{\n    public static void Main()\n    {\n        SecureString secureString = new SecureString();\n        foreach (char c in "yourSecurePassphrase")\n        {\n            secureString.AppendChar(c);\n        }\n\n        IntPtr unmanagedString = IntPtr.Zero;\n        \n        try\n        {\n            // Convert SecureString to unmanaged memory\n            unmanagedString = Marshal.SecureStringToGlobalAllocUnicode(secureString);\n\n            using (NamedPipeServerStream pipeServer = new NamedPipeServerStream("SecurePipe", PipeDirection.Out))\n            {\n                pipeServer.WaitForConnection();\n                using (StreamWriter writer = new StreamWriter(pipeServer))\n                {\n                    writer.WriteLine(Marshal.PtrToStringUni(unmanagedString));\n                }\n            }\n        }\n        finally\n        {\n            // Zero and free the unmanaged string\n            Marshal.ZeroFreeGlobalAllocUnicode(unmanagedString);\n        }\n    }\n}