<%@ Control Language="C#" Inherits="System.Web.Mvc.ViewUserControl<IEnumerable<YourNamespace.Models.CarModel>>" %>\n<% if (Model != null) { %>\n    <table>\n        <tr>\n            <th>Id</th>\n            <th>Name</th>\n            <th>MakeId</th>\n        </tr>\n        <% foreach (var item in Model) { %>\n        <tr>\n            <td><%= Html.Encode(item.Id) %></td>\n            <td><%= Html.Encode(item.Name) %></td>\n            <td><%= Html.Encode(item.MakeId) %></td>\n        </tr>\n        <% } %>\n    </table>\n<% } %>