private void AddImageButton(TableRow tRow, string letterName, string imageUrl)\n   {\n       ImageButton imgbtnRemove = new ImageButton();\n       imgbtnRemove.ID = "uxStandardLetterDeleteImage_" + letterName;\n       imgbtnRemove.CommandName = "uxStandardLetterDeleteImage_" + letterName;\n       imgbtnRemove.ImageUrl = imageUrl;\n       imgbtnRemove.AlternateText = "Remove";\n       imgbtnRemove.Click += new ImageClickEventHandler(this.ButtonClick);\n       tRow.Cells[3].Controls.Add(imgbtnRemove);\n   }