using System;\nusing System.Collections.Generic;\n\nclass Program\n{\n    static void Main()\n    {\n        List<string> days = new List<string> { "monday", "tuesday", "wednesday", "wednesday" };\n        IEnumerable<string> distinctDays = GetDistinctValues(days);\n\n        foreach (string day in distinctDays)\n        {\n            Console.WriteLine(day);\n        }\n    }\n\n    static IEnumerable<T> GetDistinctValues<T>(IEnumerable<T> list)\n    {\n        HashSet<T> seenElements = new HashSet<T>();\n        foreach (T element in list)\n        {\n            if (seenElements.Add(element))\n            {\n                yield return element;\n            }\n        }\n    }\n}