using System;\nusing System.Management;\n\nclass Program\n{\n    static void Main()\n    {\n        try\n        {\n            string query = "SELECT HotFixID, Description, InstalledOn FROM Win32_QuickFixEngineering";\n            ManagementObjectSearcher search = new ManagementObjectSearcher(query);\n            ManagementObjectCollection collection = search.Get();\n\n            foreach (ManagementObject quickFix in collection)\n            {\n                Console.WriteLine("HotFixID: {0}", quickFix["HotFixID"]);\n                Console.WriteLine("Description: {0}", quickFix["Description"]);\n                Console.WriteLine("Installed On: {0}", quickFix["InstalledOn"]);\n                Console.WriteLine();\n            }\n        }\n        catch (Exception e)\n        {\n            Console.WriteLine("An error occurred: " + e.Message);\n        }\n    }\n}