public class Baz\n{\n    public string Foo { get; set; }\n}\n\n// In your form or wherever you are setting up the DataGridView\npublic void SetupDataGridView()\n{\n    List<Baz> dataList = new List<Baz>\n    {\n        new Baz { Foo = "Example1" },\n        new Baz { Foo = "Example2" }\n    };\n\n    dataGridView1.DataSource = dataList;\n\n    // Rename the column header\n    if (dataGridView1.Columns["Foo"] != null)\n    {\n        dataGridView1.Columns["Foo"].HeaderText = "Foo bar";\n    }\n}