using System;\nusing System.Windows.Forms;\n\npublic static class InputBox\n{\n    public static string Show(string prompt, string title)\n    {\n        Form inputForm = new Form()\n        {\n            Width = 400,\n            Height = 150,\n            Text = title,\n            FormBorderStyle = FormBorderStyle.FixedDialog,\n            StartPosition = FormStartPosition.CenterScreen,\n            MinimizeBox = false,\n            MaximizeBox = false\n        };\n\n        Label textLabel = new Label() { Left = 10, Top = 20, Text = prompt, AutoSize = true };\n        TextBox textBox = new TextBox() { Left = 10, Top = 50, Width = 360 };\n        Button confirmation = new Button() { Text = "Ok", Left = 280, Width = 100, Top = 80, DialogResult = DialogResult.OK };\n\n        confirmation.Click += (sender, e) => { inputForm.Close(); };\n        inputForm.Controls.Add(textBox);\n        inputForm.Controls.Add(confirmation);\n        inputForm.Controls.Add(textLabel);\n        inputForm.AcceptButton = confirmation;\n\n        return inputForm.ShowDialog() == DialogResult.OK ? textBox.Text : string.Empty;\n    }\n}\n\n// Usage\npublic class Program\n{\n    [STAThread]\n    static void Main()\n    {\n        string userInput = InputBox.Show("Please enter your name:", "Input Box");\n        MessageBox.Show("You entered: " + userInput);\n    }\n}