using System;\nusing Microsoft.SharePoint.Client;\n\nnamespace SharePointUserRetrieval\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            string siteUrl = "https://yoursite.sharepoint.com";\n            string groupName = "YourGroupName";\n            \n            // Authentication\n            var context = new ClientContext(siteUrl);\n            context.Credentials = new SharePointOnlineCredentials("your_username@domain.com", GetSecureString("your_password"));\n\n            // Get the group by name\n            GroupCollection groupCollection = context.Web.SiteGroups;\n            context.Load(groupCollection);\n            context.ExecuteQuery();\n\n            Group group = null;\n            foreach (var grp in groupCollection)\n            {\n                if (grp.Title == groupName)\n                {\n                    group = grp;\n                    break;\n                }\n            }\n\n            if (group != null)\n            {\n                // Load users in the group\n                UserCollection userCollection = group.Users;\n                context.Load(userCollection);\n                context.ExecuteQuery();\n\n                // Display users\n                foreach (User user in userCollection)\n                {\n                    Console.WriteLine("User: " + user.Title + " (" + user.Email + ")");\n                }\n            }\n            else\n            {\n                Console.WriteLine("Group not found.");\n            }\n        }\n\n        private static System.Security.SecureString GetSecureString(string password)\n        {\n            var secureString = new System.Security.SecureString();\n            foreach (char c in password)\n            {\n                secureString.AppendChar(c);\n            }\n            secureString.MakeReadOnly();\n            return secureString;\n        }\n    }\n}