using System;\nusing Microsoft.WindowsMobile.PocketOutlook;\nusing Microsoft.WindowsMobile.PocketOutlook.MessageInterception;\n\nnamespace ViewSmsExample\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            // Retrieve the message using MAPI or any other method\n            SmsMessage smsMessage = GetSmsMessage();\n\n            // Display the message using the default viewer\n            MessagingApplication.DisplayMessage(smsMessage);\n        }\n\n        static SmsMessage GetSmsMessage()\n        {\n            // Retrieve the SMS message here\n            // For demonstration, we'll just create a dummy message\n            SmsMessage message = new SmsMessage();\n            message.Body = "This is a test message";\n            message.From = new Recipient("Sender", "1234567890");\n            message.To.Add(new Recipient("Recipient", "0987654321"));\n            message.Subject = "Test SMS";\n\n            return message;\n        }\n    }\n}