using System;\nusing System.Drawing;\nusing System.Drawing.Imaging;\nusing System.IO;\nusing PdfSharp.Pdf;\nusing PdfSharp.Drawing;\n\npublic class TiffToPdfConverter\n{\n    public void ConvertTiffToPdf(string tiffFilePath, string pdfFilePath)\n    {\n        using (var pdfDocument = new PdfDocument())\n        {\n            using (var image = Image.FromFile(tiffFilePath))\n            {\n                var frameCount = image.GetFrameCount(FrameDimension.Page);\n\n                for (var frameIdx = 0; frameIdx < frameCount; frameIdx++)\n                {\n                    image.SelectActiveFrame(FrameDimension.Page, frameIdx);\n\n                    using (var stream = new MemoryStream())\n                    {\n                        image.Save(stream, ImageFormat.Bmp);\n\n                        using (var xImage = XImage.FromStream(stream))\n                        {\n                            var page = new PdfPage\n                            {\n                                Width = xImage.PixelWidth,\n                                Height = xImage.PixelHeight\n                            };\n\n                            pdfDocument.AddPage(page);\n\n                            using (var gfx = XGraphics.FromPdfPage(page))\n                            {\n                                gfx.DrawImage(xImage, 0, 0, xImage.PixelWidth, xImage.PixelHeight);\n                            }\n                        }\n                    }\n                }\n            }\n\n            pdfDocument.Save(pdfFilePath);\n        }\n    }\n}