using System.Web.Mvc;\nusing System.Web.Mvc.Html;\n\npublic static class HtmlHelperExtensions\n{\n    public static MvcHtmlString ValidationImage(this HtmlHelper htmlHelper, string modelName)\n    {\n        var validationMessage = htmlHelper.ValidationMessage(modelName);\n        \n        if (validationMessage != null)\n        {\n            string error = htmlHelper.ViewData.ModelState[modelName]?.Errors[0].ErrorMessage;\n            string imgTag = $"<img src='/Content/Images/Error.gif' alt='Error' title='{error}' />";\n            \n            return MvcHtmlString.Create(imgTag);\n        }\n\n        return MvcHtmlString.Empty;\n    }\n}