// Base class with protected member\npublic class BaseClass\n{\n    protected int protectedMember;\n\n    public BaseClass()\n    {\n        protectedMember = 42; // Example initialization\n    }\n}\n\n// Derived class that exposes protected member through a public method\npublic class DerivedClass : BaseClass\n{\n    public int GetProtectedMember()\n    {\n        return protectedMember;\n    }\n}\n\n// Usage in a Form or elsewhere\npublic class MyForm\n{\n    public void ShowProtectedMember()\n    {\n        DerivedClass derived = new DerivedClass();\n        int value = derived.GetProtectedMember();\n        Console.WriteLine("Protected Member: " + value);\n    }\n}