void ThreadA()\n{\n    using (SqlConnection a = new SqlConnection("your_connection_string"))\n    {\n        MyStaticClass.DoStuff(a);\n    }\n}\n\nvoid ThreadB()\n{\n    using (SqlConnection b = new SqlConnection("your_connection_string"))\n    {\n        MyStaticClass.DoStuff(b);\n    }\n}\n\nstatic class MyStaticClass\n{\n    public static void DoStuff(SqlConnection sql)\n    {\n        // Ensure the connection is open\n        if (sql.State == System.Data.ConnectionState.Closed)\n        {\n            sql.Open();\n        }\n\n        // Do stuff with sql connection\n        // ...\n\n        // Optionally, you might want to close the connection here or handle it outside\n        // sql.Close();\n    }\n}