using NUnit.Framework;\nusing System;\nusing System.Windows.Forms;\n\n[TestFixture]\npublic class MapinfoWindowHandleTests\n{\n    [Test]\n    public void Constructor_ShouldAssignHandle()\n    {\n        // Arrange\n        IntPtr expectedHandle = new IntPtr(100);\n        \n        // Act\n        MapinfoWindowHandle mapinfoWindowHandle = new MapinfoWindowHandle(expectedHandle);\n        \n        // Assert\n        Assert.AreEqual(expectedHandle, ((IWin32Window)mapinfoWindowHandle).Handle);\n    }\n\n    [Test]\n    public void IWin32Window_Handle_ShouldReturnAssignedHandle()\n    {\n        // Arrange\n        IntPtr expectedHandle = new IntPtr(200);\n        MapinfoWindowHandle mapinfoWindowHandle = new MapinfoWindowHandle(expectedHandle);\n        \n        // Act\n        IntPtr actualHandle = ((IWin32Window)mapinfoWindowHandle).Handle;\n        \n        // Assert\n        Assert.AreEqual(expectedHandle, actualHandle);\n    }\n}