using Microsoft.SqlServer.Dts.Runtime;\n   using Microsoft.SqlServer.Dts.Tasks.XMLBulkLoadTask;\n\n   public class BulkLoad\n   {\n       public static void Main()\n       {\n           XMLBulkLoadTask bulkLoad = new XMLBulkLoadTask();\n           bulkLoad.Connection = "YourConnectionString";\n           bulkLoad.XMLSchemaFile = "your-schema.xsd";\n           bulkLoad.XMLDataFile = "your-data.xml";\n           bulkLoad.DestinationTable = "YourTableName";\n           bulkLoad.Execute();\n       }\n   }