using System;\nusing System.Web;\n\npublic class WebService : System.Web.Services.WebService\n{\n    [WebMethod]\n    public string MyWebServiceMethod()\n    {\n        string clientIPAddress = HttpContext.Current.Request.UserHostAddress;\n        int clientPort = HttpContext.Current.Request.ServerVariables["REMOTE_PORT"] != null ? \n                         int.Parse(HttpContext.Current.Request.ServerVariables["REMOTE_PORT"]) : \n                         -1;\n\n        // For logging purposes\n        string logMessage = $"Client IP: {clientIPAddress}, Client Port: {clientPort}";\n        // Log the message\n        // Log(logMessage); // Implement your logging mechanism\n\n        return "Success";\n    }\n}