using System;\n\npublic class ArraySubsetCheck\n{\n    public static bool IsSubset(byte[] largerArray, byte[] smallerArray)\n    {\n        if (largerArray == null || smallerArray == null || smallerArray.Length == 0 || largerArray.Length < smallerArray.Length)\n        {\n            return false;\n        }\n\n        for (int i = 0; i <= largerArray.Length - smallerArray.Length; i++)\n        {\n            bool match = true;\n            for (int j = 0; j < smallerArray.Length; j++)\n            {\n                if (largerArray[i + j] != smallerArray[j])\n                {\n                    match = false;\n                    break;\n                }\n            }\n\n            if (match)\n            {\n                return true;\n            }\n        }\n\n        return false;\n    }\n\n    public static void Main()\n    {\n        byte[] largerArray = { 1, 2, 3, 4, 5, 6, 7, 8, 9 };\n        byte[] smallerArray = { 4, 5, 6 };\n\n        bool result = IsSubset(largerArray, smallerArray);\n        Console.WriteLine($"Is the smaller array a subset of the larger array? {result}");\n    }\n}