public void InactiveCustomers(IEnumerable<Guid> customerIDs)\n{\n    // Convert the list of Guids to a list of strings\n    var idList = customerIDs.Select(id => $"'{id}'").ToList();\n    // Join the list into a single comma-separated string\n    string joinedIds = string.Join(",", idList);\n\n    // Construct the SQL command\n    string commandText = $"UPDATE Customer SET Active = 0 WHERE CustomerID IN ({joinedIds})";\n\n    using (SqlConnection conn = new SqlConnection(connectionString))\n    {\n        using (SqlCommand myAdoCommand = new SqlCommand(commandText, conn))\n        {\n            conn.Open();\n            myAdoCommand.ExecuteNonQuery();\n        }\n    }\n}