public bool ValidateSource(Source srcToValidate)\n{\n    if (string.IsNullOrEmpty(srcToValidate.Name))\n        _validationDictionary.AddError("Name", "Name is required.");\n    else if (srcToValidate.Name.Trim().Length == 0)\n        _validationDictionary.AddError("Name", "Name is required.");\n\n    if (string.IsNullOrEmpty(srcToValidate.Url))\n        _validationDictionary.AddError("Url", "Url is required.");\n    else if (srcToValidate.Url.Trim().Length == 0)\n        _validationDictionary.AddError("Url", "Url is required.");\n\n    // Add custom validation logic here, e.g., checking database for uniqueness\n    if (!IsUrlUnique(srcToValidate.Url))\n        _validationDictionary.AddError("Url", "Url must be unique.");\n\n    return _validationDictionary.IsValid;\n}