using Microsoft.Office.Interop.Visio;\n\npublic class VisioFacade\n{\n    private Application visioApplication;\n    private Documents visioDocuments;\n    private Document visioDocument;\n    private Pages visioPages;\n    private Page visioPage;\n\n    public VisioFacade()\n    {\n        visioApplication = new Application();\n        visioDocuments = visioApplication.Documents;\n    }\n\n    public void OpenDocument(string path)\n    {\n        visioDocument = visioDocuments.Open(path);\n        visioPages = visioDocument.Pages;\n    }\n\n    public void AddShape(string masterName, double x, double y)\n    {\n        visioPage = visioApplication.ActivePage;\n        Master master = visioApplication.Documents[masterName].Masters[masterName];\n        visioPage.Drop(master, x, y);\n    }\n\n    // Additional methods to hide complexity and provide simplified access\n}