protected void Page_Load(object sender, EventArgs e)\n   {\n       if (!IsPostBack)\n       {\n           LoadUserPreferences();\n           ConfigureDataGrid();\n       }\n   }\n\n   private void LoadUserPreferences()\n   {\n       string selectedColumns = Profile.SelectedColumns;\n       if (!string.IsNullOrEmpty(selectedColumns))\n       {\n           string[] columns = selectedColumns.Split(',');\n           foreach (string column in columns)\n           {\n               ListItem item = chkColumns.Items.FindByValue(column);\n               if (item != null)\n               {\n                   item.Selected = true;\n               }\n           }\n       }\n   }\n\n   private void ConfigureDataGrid()\n   {\n       string selectedColumns = Profile.SelectedColumns;\n       if (!string.IsNullOrEmpty(selectedColumns))\n       {\n           string[] columns = selectedColumns.Split(',');\n\n           // Assuming you have a DataGrid named dataGrid\n           foreach (DataControlField column in dataGrid.Columns)\n           {\n               if (columns.Contains(column.HeaderText))\n               {\n                   column.Visible = true;\n               }\n               else\n               {\n                   column.Visible = false;\n               }\n           }\n       }\n   }