using System;\nusing System.Collections.Generic;\n\npublic class Node\n{\n    public int Value;\n    public Node left;\n    public Node right;\n\n    public Node(int value)\n    {\n        Value = value;\n        left = null;\n        right = null;\n    }\n}\n\npublic class BinaryTree\n{\n    public Node root;\n\n    public void BFS()\n    {\n        if (root == null)\n        {\n            return;\n        }\n\n        Queue<Node> q = new Queue<Node>();\n        q.Enqueue(root);\n\n        while (q.Count > 0)\n        {\n            Node n = q.Dequeue();\n            Console.WriteLine(n.Value);\n\n            if (n.left != null)\n            {\n                q.Enqueue(n.left);\n            }\n\n            if (n.right != null)\n            {\n                q.Enqueue(n.right);\n            }\n        }\n    }\n}