// Pseudo-code for setting up subscription with parameters\npublic void CreateSubscription(string reportPath, string subscriptionName, string parameterName, string parameterValue, string deliveryMethod, Schedule schedule) {\n    ReportSubscription subscription = new ReportSubscription();\n    subscription.ReportPath = reportPath;\n    subscription.Name = subscriptionName;\n    subscription.Parameters.Add(new ReportParameter(parameterName, parameterValue));\n    subscription.DeliveryMethod = deliveryMethod;\n    subscription.Schedule = schedule;\n\n    // Save the subscription\n    ReportService.SaveSubscription(subscription);\n}