using DbUp;\nusing System;\nusing System.Linq;\n\nclass Program\n{\n    static int Main(string[] args)\n    {\n        var connectionString = args.FirstOrDefault() ?? "Server=localhost; Database=mydb; Trusted_Connection=true;";\n\n        var upgrader =\n            DeployChanges.To\n                .SqlDatabase(connectionString)\n                .WithScriptsFromFileSystem(@"C:\path\to\scripts")\n                .LogToConsole()\n                .Build();\n\n        var result = upgrader.PerformUpgrade();\n\n        if (!result.Successful)\n        {\n            Console.ForegroundColor = ConsoleColor.Red;\n            Console.WriteLine(result.Error);\n            Console.ResetColor();\n            return -1;\n        }\n\n        Console.ForegroundColor = ConsoleColor.Green;\n        Console.WriteLine("Success!");\n        Console.ResetColor();\n        return 0;\n    }\n}