using System;\nusing System.IO;\nusing iTextSharp.text;\nusing iTextSharp.text.pdf;\n\npublic void GeneratePdfAndSaveToFileSystem(string htmlContent, string pdfDirectory)\n{\n    // Ensure the directory exists\n    if (!Directory.Exists(pdfDirectory))\n    {\n        Directory.CreateDirectory(pdfDirectory);\n    }\n\n    // Generate a unique filename\n    string fileName = Path.Combine(pdfDirectory, $"{Guid.NewGuid()}.pdf");\n\n    using (FileStream fs = new FileStream(fileName, FileMode.Create, FileAccess.Write, FileShare.None))\n    {\n        // Create a new document\n        Document document = new Document();\n        PdfWriter writer = PdfWriter.GetInstance(document, fs);\n\n        document.Open();\n        // Add the HTML content to the PDF\n        using (StringReader sr = new StringReader(htmlContent))\n        {\n            XMLWorkerHelper.GetInstance().ParseXHtml(writer, document, sr);\n        }\n\n        document.Close();\n    }\n\n    // Return the relative path of the PDF file to create a link\n    string relativePath = $"~/PDFs/{Path.GetFileName(fileName)}";\n    // Use the relative path in your HTML to create the download link\n    System.Web.HttpContext.Current.Response.Write($"<a href='{relativePath}'>Download PDF</a>");\n}