using System;\nusing System.Text.RegularExpressions;\n\npublic static class StringExtensions\n{\n    public static string EnsureWordLength(this string target, int length)\n    {\n        // Regular expression to match HTML tags\n        string htmlTagPattern = @"<[^>]+>";\n        string plainText = Regex.Replace(target, htmlTagPattern, string.Empty);\n\n        // Split the plain text into chunks of the specified length\n        int currentIndex = 0;\n        int plainTextLength = plainText.Length;\n        string[] result = new string[(plainTextLength + length - 1) / length];\n\n        for (int i = 0; i < result.Length; i++)\n        {\n            int endIndex = Math.Min(currentIndex + length, plainTextLength);\n            result[i] = plainText.Substring(currentIndex, endIndex - currentIndex);\n            currentIndex = endIndex;\n        }\n\n        // Reinsert HTML tags at their original positions\n        foreach (Match match in Regex.Matches(target, htmlTagPattern))\n        {\n            int position = match.Index;\n            string tag = match.Value;\n            int indexCounter = 0;\n\n            for (int i = 0; i < result.Length; i++)\n            {\n                if (position < length * (i + 1))\n                {\n                    result[i] = result[i].Insert(position - indexCounter, tag);\n                    indexCounter += tag.Length;\n                    position = int.MaxValue; // To break out of the loop\n                }\n                else\n                {\n                    indexCounter += length;\n                }\n            }\n        }\n\n        return string.Join("\n", result);\n    }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        string input = "Hellotoevryone<img height=\"115\" width=\"150\" alt=\"\" src=\"/Content/Edt/image/b4976875-8dfb-444c-8b32-cc b47b2d81e0.jpg\" />Iamsogladtoseeall.\n";\n        string result = input.EnsureWordLength(10);\n        Console.WriteLine(result);\n    }\n}