// Number of random items to retrieve\nint numberOfRandomItems = 10; // Example value\n\n// SQL command to retrieve random ItemIDs\nstring sqlQuery = "SELECT TOP (@NumberOfRandomItems) ItemID FROM item_k ORDER BY NEWID()";\n\nusing (SqlCommand retrieveComm = new SqlCommand(sqlQuery, searchCon))\n{\n    // Add parameter to prevent SQL injection and specify the number of random items\n    retrieveComm.Parameters.AddWithValue("@NumberOfRandomItems", numberOfRandomItems);\n\n    // Execute the command and process the results\n    using (SqlDataReader reader = retrieveComm.ExecuteReader())\n    {\n        while (reader.Read())\n        {\n            int randomItemID = reader.GetInt32(0);\n            // Process the randomItemID as needed\n            Console.WriteLine(randomItemID);\n        }\n    }\n}