using System;\nusing System.Diagnostics;\nusing System.Windows.Forms;\n\npublic class MainForm : Form\n{\n    private TextBox outputTextBox;\n    private Button startButton;\n\n    public MainForm()\n    {\n        outputTextBox = new TextBox\n        {\n            Multiline = true,\n            Dock = DockStyle.Fill,\n            ScrollBars = ScrollBars.Vertical\n        };\n        startButton = new Button\n        {\n            Text = "Start Console App",\n            Dock = DockStyle.Top\n        };\n        startButton.Click += StartButton_Click;\n\n        Controls.Add(outputTextBox);\n        Controls.Add(startButton);\n    }\n\n    private void StartButton_Click(object sender, EventArgs e)\n    {\n        StartConsoleApp();\n    }\n\n    private void StartConsoleApp()\n    {\n        var p = new Process\n        {\n            StartInfo = new ProcessStartInfo\n            {\n                FileName = @"C:\ConsoleApp.exe",\n                UseShellExecute = false,\n                RedirectStandardOutput = true,\n                RedirectStandardError = true,\n                CreateNoWindow = true\n            },\n            EnableRaisingEvents = true\n        };\n\n        p.OutputDataReceived += (sendingProcess, outLine) =>\n        {\n            if (outLine.Data != null)\n            {\n                AppendTextBox(outLine.Data);\n            }\n        };\n\n        p.ErrorDataReceived += (sendingProcess, errLine) =>\n        {\n            if (errLine.Data != null)\n            {\n                AppendTextBox(errLine.Data);\n            }\n        };\n\n        p.Start();\n        p.BeginOutputReadLine();\n        p.BeginErrorReadLine();\n    }\n\n    private void AppendTextBox(string text)\n    {\n        if (outputTextBox.InvokeRequired)\n        {\n            outputTextBox.Invoke(new Action<string>(AppendTextBox), text);\n        }\n        else\n        {\n            outputTextBox.AppendText(text + Environment.NewLine);\n        }\n    }\n\n    [STAThread]\n    public static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.SetCompatibleTextRenderingDefault(false);\n        Application.Run(new MainForm());\n    }\n}