private void Execute(string sPath)\n{\n    try\n    {\n        // Validate the file path\n        if (!System.IO.File.Exists(sPath))\n        {\n            throw new FileNotFoundException("Executable file not found.");\n        }\n\n        System.Diagnostics.Process proc = new System.Diagnostics.Process();\n        proc.StartInfo.WorkingDirectory = ConfigurationManager.AppSettings["WORKINGDIRECTORY"].ToString();\n        proc.StartInfo.RedirectStandardOutput = true;\n        proc.StartInfo.RedirectStandardError = true;\n        proc.StartInfo.UseShellExecute = false;\n        proc.StartInfo.FileName = sPath;\n\n        // Log the start of the process\n        System.Diagnostics.Debug.WriteLine($"Starting process: {sPath}");\n\n        proc.Start();\n        proc.WaitForExit();\n\n        // Read outputs and log them\n        string result = proc.StandardOutput.ReadToEnd();\n        string error = proc.StandardError.ReadToEnd();\n        int exitCode = proc.ExitCode;\n\n        // Log the results\n        System.Diagnostics.Debug.WriteLine($"Process output: {result}");\n        System.Diagnostics.Debug.WriteLine($"Process error: {error}");\n        System.Diagnostics.Debug.WriteLine($"Process exit code: {exitCode}");\n\n        // Output the result\n        Response.Write(result + " - "  + exitCode);\n\n        proc.Close();\n    }\n    catch (Exception ex)\n    {\n        // Log exception details\n        System.Diagnostics.Debug.WriteLine($"Error executing process: {ex.Message}");\n        Response.Write("An error occurred: " + ex.Message);\n    }\n}