int batchSize = 1000;\nint totalRecords = dbContext.SourceTable.Count();\nfor (int i = 0; i < totalRecords; i += batchSize)\n{\n    var records = dbContext.SourceTable\n                           .OrderBy(r => r.Id)\n                           .Skip(i)\n                           .Take(batchSize)\n                           .ToList();\n\n    foreach (var record in records)\n    {\n        // Perform your calculations and transfer logic here\n\n        // Example: Transfer record to destination table\n        var newRecord = new DestinationTable\n        {\n            // Map properties from source record to destination record\n        };\n        dbContext.DestinationTable.Add(newRecord);\n    }\n\n    dbContext.SaveChanges();\n}