[ServiceContract]\npublic interface IMyService\n{\n    [OperationContract]\n    [FaultContract(typeof(ValidationFault))]\n    void MyServiceMethod(string input);\n}\n\npublic class MyService : IMyService\n{\n    public void MyServiceMethod(string input)\n    {\n        // Perform validation\n        if (string.IsNullOrEmpty(input))\n        {\n            var fault = new ValidationFault(Guid.NewGuid(), "input", "Input cannot be null or empty.", true);\n            throw new FaultException<ValidationFault>(fault, new FaultReason("Validation error"));\n        }\n        \n        // Actual method implementation\n    }\n}