public void ValidateStringLengths(Dictionary<string, string> values)\n   {\n       var metadata = /* Fetch your metadata list from where you stored it */;\n       foreach (var value in values)\n       {\n           var column = metadata.FirstOrDefault(c => c.ColumnName == value.Key);\n           if (column != null && value.Value.Length > column.MaxLength)\n           {\n               throw new ArgumentException($"Value for {value.Key} exceeds maximum length of {column.MaxLength}");\n           }\n       }\n   }