using System;\nusing System.Security.Principal;\n\npublic class SIDResolver\n{\n    public static string GetUsernameFromSID(string sidString)\n    {\n        try\n        {\n            SecurityIdentifier sid = new SecurityIdentifier(sidString);\n            NTAccount account = (NTAccount)sid.Translate(typeof(NTAccount));\n            return account.ToString();\n        }\n        catch (Exception ex)\n        {\n            // Handle exceptions (e.g., invalid SID format, SID not found, etc.)\n            Console.WriteLine($"Error: {ex.Message}");\n            return null;\n        }\n    }\n\n    public static void Main()\n    {\n        string sidString = "S-1-5-21-..."; // Example SID\n        string username = GetUsernameFromSID(sidString);\n        \n        if (username != null)\n        {\n            Console.WriteLine($"The username for SID {sidString} is {username}");\n        }\n        else\n        {\n            Console.WriteLine($"Failed to resolve the username for SID {sidString}");\n        }\n    }\n}