using System;\nusing System.Reflection;\nusing System.Runtime.InteropServices;\n\nnamespace COMInteropExample\n{\n    [ComImport, Guid("D30C1661-CDAF-11D0-8A3E-00C04FC9E26E"), InterfaceType(ComInterfaceType.InterfaceIsIDispatch)]\n    public interface IWebBrowser2\n    {\n        [DispId(500)]\n        void Navigate2([In] ref object URL, [In] ref object Flags, [In] ref object TargetFrameName, [In] ref object PostData, [In] ref object Headers);\n\n        [DispId(0xcb)]\n        object Document { [return: MarshalAs(UnmanagedType.IDispatch)] get; }\n        \n        bool Visible { [DispId(0x22e)] set; }\n    }\n\n    [ComImport, Guid("0002DF01-0000-0000-C000-000000000046")]\n    public class InternetExplorer\n    {\n    }\n\n    public class Program\n    {\n        public static void SpawnIEWithSource(string szHtml)\n        {\n            IWebBrowser2 ie = (IWebBrowser2)new InternetExplorer();\n\n            // Navigate to about:blank to initialize the browser\n            object url = "about:blank";\n            object flags = Missing.Value;\n            object targetFrameName = Missing.Value;\n            object postData = Missing.Value;\n            object headers = Missing.Value;\n\n            ie.Navigate2(ref url, ref flags, ref targetFrameName, ref postData, ref headers);\n\n            // Wait for the browser to load the blank page\n            System.Threading.Thread.Sleep(1000);\n\n            // Get the document object\n            object doc = ie.Document;\n\n            // Invoke the Write method on the document\n            Type docType = doc.GetType();\n            object[] writeParams = new object[] { new object[] { szHtml } };\n            docType.InvokeMember("write", BindingFlags.InvokeMethod, null, doc, writeParams);\n\n            // Invoke the Close method on the document\n            docType.InvokeMember("close", BindingFlags.InvokeMethod, null, doc, null);\n\n            // Make the browser visible\n            ie.Visible = true;\n        }\n\n        static void Main(string[] args)\n        {\n            string htmlContent = "<html><body><h1>Hello, World!</h1></body></html>";\n            SpawnIEWithSource(htmlContent);\n        }\n    }\n}