// Example of storing session data in a database\npublic class SessionDataService\n{\n    private readonly string _connectionString;\n\n    public SessionDataService(string connectionString)\n    {\n        _connectionString = connectionString;\n    }\n\n    public void SaveSessionData(string sessionId, string data)\n    {\n        using (var connection = new SqlConnection(_connectionString))\n        {\n            var command = new SqlCommand("INSERT INTO SessionData (SessionId, Data) VALUES (@SessionId, @Data)", connection);\n            command.Parameters.AddWithValue("@SessionId", sessionId);\n            command.Parameters.AddWithValue("@Data", data);\n            connection.Open();\n            command.ExecuteNonQuery();\n        }\n    }\n\n    public string GetSessionData(string sessionId)\n    {\n        using (var connection = new SqlConnection(_connectionString))\n        {\n            var command = new SqlCommand("SELECT Data FROM SessionData WHERE SessionId = @SessionId", connection);\n            command.Parameters.AddWithValue("@SessionId", sessionId);\n            connection.Open();\n            var result = command.ExecuteScalar();\n            return result?.ToString();\n        }\n    }\n}