public void InactiveCustomers(IEnumerable<Guid> customerIDs)\n{\n    // Convert the IEnumerable<Guid> to a DataTable\n    DataTable customerIdTable = new DataTable();\n    customerIdTable.Columns.Add("CustomerID", typeof(Guid));\n    foreach (var id in customerIDs)\n    {\n        customerIdTable.Rows.Add(id);\n    }\n\n    using (SqlConnection conn = new SqlConnection(connectionString))\n    {\n        using (SqlCommand myAdoCommand = new SqlCommand("UPDATE Customer SET Active = 0 WHERE CustomerID IN (SELECT CustomerID FROM @CustomerIDs)", conn))\n        {\n            // Add table-valued parameter\n            SqlParameter tvpParam = new SqlParameter\n            {\n                ParameterName = "@CustomerIDs",\n                SqlDbType = SqlDbType.Structured,\n                TypeName = "dbo.GuidList",\n                Value = customerIdTable\n            };\n            myAdoCommand.Parameters.Add(tvpParam);\n\n            conn.Open();\n            myAdoCommand.ExecuteNonQuery();\n        }\n    }\n}