using System;\nusing System.Collections.Generic;\nusing System.Xml;\n\npublic class XmlNodeEqualityComparer : IEqualityComparer<XmlNode>\n{\n    public bool Equals(XmlNode x, XmlNode y)\n    {\n        if (x == null || y == null)\n            return x == y;\n\n        if (x.Name != y.Name || x.Attributes.Count != y.Attributes.Count)\n            return false;\n\n        // Compare attributes\n        foreach (XmlAttribute attr in x.Attributes)\n        {\n            var correspondingAttr = y.Attributes[attr.Name];\n            if (correspondingAttr == null || attr.Value != correspondingAttr.Value)\n                return false;\n        }\n\n        // Compare children\n        if (x.ChildNodes.Count != y.ChildNodes.Count)\n            return false;\n\n        for (int i = 0; i < x.ChildNodes.Count; i++)\n        {\n            if (!Equals(x.ChildNodes[i], y.ChildNodes[i]))\n                return false;\n        }\n\n        return true;\n    }\n\n    public int GetHashCode(XmlNode obj)\n    {\n        if (obj == null)\n            return 0;\n\n        int hash = obj.Name.GetHashCode();\n        foreach (XmlAttribute attr in obj.Attributes)\n        {\n            hash ^= attr.Name.GetHashCode();\n            hash ^= attr.Value.GetHashCode();\n        }\n\n        foreach (XmlNode child in obj.ChildNodes)\n        {\n            hash ^= GetHashCode(child);\n        }\n\n        return hash;\n    }\n}\n\n// Usage example\npublic class Program\n{\n    public static void Main()\n    {\n        var xml1 = "<w:p><w:pPr><w:spacing w:after='120'/></w:pPr><w:r><w:t>Hello</w:t></w:r></w:p>";\n        var xml2 = "<w:p><w:pPr><w:spacing w:after='120'/></w:pPr><w:r><w:t>Hello</w:t></w:r></w:p>";\n\n        XmlDocument doc1 = new XmlDocument();\n        doc1.LoadXml(xml1);\n        XmlNode node1 = doc1.DocumentElement;\n\n        XmlDocument doc2 = new XmlDocument();\n        doc2.LoadXml(xml2);\n        XmlNode node2 = doc2.DocumentElement;\n\n        XmlNodeEqualityComparer comparer = new XmlNodeEqualityComparer();\n        bool areEqual = comparer.Equals(node1, node2);\n\n        Console.WriteLine("Nodes are equal: " + areEqual);\n    }\n}