using System;\nusing System.Data;\n\npublic class Program\n{\n    public static void Main()\n    {\n        // Assuming you have an existing DataSet with a DataTable "OriginalTable"\n        DataSet originalDataSet = new DataSet();\n        DataTable originalTable = new DataTable("OriginalTable");\n\n        // Add some columns to the original table\n        originalTable.Columns.Add("Column1", typeof(string));\n        originalTable.Columns.Add("Column2", typeof(int));\n        originalTable.Columns.Add("Column3", typeof(DateTime));\n\n        // Add some rows to the original table\n        originalTable.Rows.Add("Row1", 1, DateTime.Now);\n        originalTable.Rows.Add("Row2", 2, DateTime.Now);\n\n        // Add the original table to the dataset\n        originalDataSet.Tables.Add(originalTable);\n\n        // Create a new DataTable with only the desired columns\n        DataTable filteredTable = new DataTable("FilteredTable");\n        filteredTable.Columns.Add("Column1", typeof(string)); // Only add desired columns\n        filteredTable.Columns.Add("Column2", typeof(int));    // Only add desired columns\n\n        // Copy data from the original table to the new filtered table\n        foreach (DataRow row in originalTable.Rows)\n        {\n            filteredTable.Rows.Add(row["Column1"], row["Column2"]);\n        }\n\n        // Create a new DataSet with the filtered table\n        DataSet filteredDataSet = new DataSet();\n        filteredDataSet.Tables.Add(filteredTable);\n\n        // Write the filtered dataset to XML\n        filteredDataSet.WriteXml("filtered_output.xml");\n\n        Console.WriteLine("Filtered XML has been written to filtered_output.xml");\n    }\n}