[WebMethod]\npublic string CallRemoteWebService()\n{\n    // Create an instance of the remote web service proxy\n    RemoteWebService remoteService = new RemoteWebService();\n\n    // Set the credentials to the current security context (impersonated user)\n    remoteService.Credentials = System.Net.CredentialCache.DefaultCredentials;\n\n    // Call the remote web service method\n    return remoteService.SomeMethod();\n}