protected void Application_Error(Object sender, EventArgs e)\n{\n    Exception exception = Server.GetLastError();\n    \n    if (exception != null)\n    {\n        // Check if the exception is a "The client disconnected." error\n        if (exception is HttpException httpException && httpException.ErrorCode == -2147023901)\n        {\n            // Ignore the "The client disconnected." error\n            Server.ClearError();\n            return;\n        }\n\n        // Log and email other exceptions\n        LogException(exception);\n        EmailException(exception);\n    }\n}\n\nprivate void LogException(Exception exception)\n{\n    // Your logging logic here\n}\n\nprivate void EmailException(Exception exception)\n{\n    // Your email logic here\n}