using (var context = new YourDataContext())\n{\n    var query = from item in context.YourTable\n                group item by item.DateTimeColumn.Hour into g\n                select new\n                {\n                    Hour = g.Key,\n                    Items = g.ToList()\n                };\n\n    foreach (var group in query)\n    {\n        Console.WriteLine("Hour: " + group.Hour);\n        foreach (var item in group.Items)\n        {\n            Console.WriteLine(item); // Replace with relevant display code\n        }\n    }\n}