protected void btnSubmit_Click(object sender, EventArgs e)\n{\n    foreach (GridViewRow row in gridView.Rows)\n    {\n        // Find the checkbox and textbox in the template fields\n        CheckBox chkSelected = (CheckBox)row.FindControl("chkSelect");\n        TextBox txtQuantity = (TextBox)row.FindControl("txtQuantity");\n\n        if (chkSelected != null && chkSelected.Checked)\n        {\n            // Get the value from the textbox\n            int enteredQuantity;\n            if (int.TryParse(txtQuantity.Text, out enteredQuantity))\n            {\n                // Get the databound field value\n                int availableQuantity = Convert.ToInt32(((DataRowView)row.DataItem)["Quantity"]);\n\n                // Compare the values and carry out the order function if valid\n                if (enteredQuantity <= availableQuantity)\n                {\n                    // Call your order function here\n                    ProcessOrder(row, enteredQuantity);\n                }\n                else\n                {\n                    // Handle the case where entered quantity is more than available\n                    lblMessage.Text = "Entered quantity exceeds available quantity for item " + row.RowIndex;\n                }\n            }\n            else\n            {\n                // Handle invalid quantity input\n                lblMessage.Text = "Please enter a valid quantity for item " + row.RowIndex;\n            }\n        }\n    }\n}\n\nprivate void ProcessOrder(GridViewRow row, int quantity)\n{\n    // Your order processing logic here\n    // For example, you might want to extract other details from the row and save the order\n}