using (var context = new MyDbContext())\n   {\n       // Add a new person\n       var person = new Person { FirstName = "John", LastName = "Doe" };\n       context.People.Add(person);\n       context.SaveChanges();\n\n       // Check if a column exists\n       var columnExists = context.Database.SqlQuery<int>("SELECT COUNT(*) FROM INFORMATION_SCHEMA.COLUMNS WHERE TABLE_NAME = 'People' AND COLUMN_NAME = 'Company'").Single() > 0;\n       if (columnExists)\n       {\n           // Remove a column (requires a migration)\n           context.Database.ExecuteSqlCommand("ALTER TABLE People DROP COLUMN Company");\n       }\n   }