using System;\nusing System.Net;\nusing System.Net.Sockets;\nusing System.Net.Security;\nusing System.Security.Cryptography.X509Certificates;\nusing System.Text;\n\nclass Program\n{\n    static void Main()\n    {\n        var listener = new TcpListener(IPAddress.Any, 12345);\n        listener.Start();\n        Console.WriteLine("Server started...");\n\n        while (true)\n        {\n            var client = listener.AcceptTcpClient();\n            Console.WriteLine("Client connected...");\n            var sslStream = new SslStream(client.GetStream(), false);\n            sslStream.AuthenticateAsServer(GetServerCertificate(), clientCertificateRequired: false, checkCertificateRevocation: false);\n\n            byte[] buffer = new byte[4096];\n            int bytesRead = sslStream.Read(buffer, 0, buffer.Length);\n            string request = Encoding.UTF8.GetString(buffer, 0, bytesRead);\n            Console.WriteLine($"Received: {request}");\n\n            string response = "Hello from server!";\n            byte[] responseBytes = Encoding.UTF8.GetBytes(response);\n            sslStream.Write(responseBytes);\n            sslStream.Flush();\n        }\n    }\n\n    static X509Certificate GetServerCertificate()\n    {\n        // Load your server certificate here\n        return new X509Certificate("path_to_certificate.pfx", "certificate_password");\n    }\n}