using System;\nusing System.IO;\n\npublic class ConfigFileManager\n{\n    private static readonly string ConfigFileName = "shared_config.txt";\n    private static readonly string ConfigFilePath = Path.Combine(Environment.GetFolderPath(Environment.SpecialFolder.ApplicationData), ConfigFileName);\n\n    public static string ReadConfig()\n    {\n        if (File.Exists(ConfigFilePath))\n        {\n            return File.ReadAllText(ConfigFilePath);\n        }\n        else\n        {\n            throw new FileNotFoundException("Configuration file not found.");\n        }\n    }\n\n    public static void WriteConfig(string content)\n    {\n        File.WriteAllText(ConfigFilePath, content);\n    }\n}\n\n// Usage example\nclass Program\n{\n    static void Main()\n    {\n        // Writing to the config file\n        ConfigFileManager.WriteConfig("Your configuration settings");\n\n        // Reading from the config file\n        string config = ConfigFileManager.ReadConfig();\n        Console.WriteLine(config);\n    }\n}